mapper.select:
SELECT * FROM sys_attachment 
WHERE business_type = #{businessType} AND business_id = #{businessId}
ORDER BY upload_time DESC

mapper.update:
UPDATE sys_attachment
file_name = #{fileName}, file_path = #{filePath}, file_size = #{fileSize}, file_type = #{fileType}, remark = #{remark}, business_id = #{businessId},
WHERE attachment_id = #{attachmentId}

UPDATE sys_attachment
SET business_id = #{businessId}
WHERE attachment_id = #{attachmentId}

UPDATE sys_attachment
SET business_id = #{businessId}
WHERE business_type = #{businessType} AND business_id IS NULL

    AND attachment_id IN
    
        #{id}
