emp_name = #{empName}, emp_sex = #{empSex}, id_card = #{idCard}, emp_birthday = #{empBirthday}, emp_email = #{empEmail}, emp_phone = #{empPhone}, dept_id = #{deptId}, dept_code = #{deptCode}, is_stop = #{isStop}, emp_type_id = #{empTypeId}, bank_account = #{bankAccount}, bank_name = #{bankName}, photo = #{photo},
WHERE emp_code = #{empCode}
