mapper.select:
SELECT * FROM wf_process_definition
ORDER BY definition_type, version DESC
LIMIT #{offset}, #{size}

SELECT * FROM wf_process_definition WHERE definition_type = #{definitionType}
ORDER BY version DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE wf_process_definition
definition_name = #{definitionName}, definition_type = #{definitionType}, business_type = #{businessType}, process_xml = #{processXml}, process_json = #{processJson}, version = #{version}, is_active = #{isActive}, description = #{description}, update_time = NOW()
WHERE definition_id = #{definitionId}
