mapper.select:
SELECT * FROM budget_category
WHERE category_level = 1

    AND budget_year = #{budgetYear}


    AND category_type = #{categoryType}

ORDER BY category_code

SELECT * FROM budget_category
WHERE category_level = 2

    AND budget_year = #{budgetYear}


    AND category_type = #{categoryType}


    AND parent_category_id = #{parentCategoryId}

ORDER BY category_code

mapper.update:
UPDATE budget_category
category_name = #{categoryName}, budget_year = #{budgetYear}, category_type = #{categoryType}, parent_category_id = #{parentCategoryId}, parent_category_code = #{parentCategoryCode}, is_stop = #{isStop}, update_time = NOW()
WHERE category_id = #{categoryId}

mapper.select:
SELECT MAX(category_code) FROM budget_category
WHERE category_level = 1
  AND budget_year = #{budgetYear}
  AND category_type = #{categoryType}

SELECT MAX(category_code) FROM budget_category
WHERE category_level = 2
  AND parent_category_code = #{parentCategoryCode}
