mapper.select:
SELECT bi.item_id, bi.item_code, bi.item_name, bi.budget_year, bi.category_id, bi.category_code, bi.category_type, bi.parent_category_id, bi.parent_category_code, bc1.category_name AS level1_category_name, bc2.category_name AS level2_category_name, bi.account_subject, bi.is_centralized, bi.allow_adjust, bi.item_desc, bi.is_stop, bi.create_user, bi.create_time, bi.update_time FROM budget_item bi LEFT JOIN budget_category bc2 ON bi.parent_category_id = bc2.category_id LEFT JOIN budget_category bc1 ON bc2.parent_category_id = bc1.category_id WHERE 1=1
AND bi.budget_year = #{budgetYear}
AND bi.category_type = #{categoryType}
AND bc1.category_id = #{level1CategoryId}
AND bi.parent_category_id = #{level2CategoryId}
AND bi.item_name LIKE CONCAT('%', #{itemName}, '%')
ORDER BY bi.budget_year DESC, bi.item_code

SELECT MAX(item_code) 
FROM budget_item 
WHERE parent_category_code = #{categoryCode}

mapper.update:
UPDATE budget_item
item_name = #{itemName}, budget_year = #{budgetYear}, category_id = #{categoryId}, category_code = #{categoryCode}, category_type = #{categoryType}, parent_category_id = #{parentCategoryId}, parent_category_code = #{parentCategoryCode}, account_subject = #{accountSubject}, is_centralized = #{isCentralized}, allow_adjust = #{allowAdjust}, item_desc = #{itemDesc}, is_stop = #{isStop},
WHERE item_id = #{itemId}
