mapper.update:
UPDATE budget
budget_name = #{budgetName}, budget_amount = #{budgetAmount}, occupied_amount = #{occupiedAmount}, executed_amount = #{executedAmount}, remaining_amount = #{remainingAmount}, execution_rate = #{executionRate}, status = #{status}, process_definition_id = #{processDefinitionId}, process_instance_id = #{processInstanceId},
WHERE budget_id = #{budgetId}
